    /**
     * Counts the occurrences of each unique element in the array
     * @param arr Input integer array
     * @return Map of unique elements and their counts
     * @throws IllegalArgumentException if array contains non-integer elements
     */
    public static Map<Integer, Integer> countRecurrentEntities(int[] arr) {
        // Validate input array
        validateIntegerArray(arr);

        // Use HashMap to efficiently track element counts
        Map<Integer, Integer> countMap = new HashMap<>();

        for (int num : arr) {
            countMap.put(num, countMap.getOrDefault(num, 0) + 1);
        }

        // Filter out non-recurrent elements
        return filterRecurrentEntities(countMap);
    }

    /**
     * Validates that all array elements are integers
     * @param arr Input array to validate
     * @throws IllegalArgumentException if non-integer elements are found
     */
    private static void validateIntegerArray(int[] arr) {
        for (int num : arr) {
            if (!isValidInteger(num)) {
                throw new IllegalArgumentException("Array contains non-integer elements");
            }
        }
    }

    /**
     * Checks if the element is a valid integer
     * @param num Element to validate
     * @return boolean indicating if element is a valid integer
     */
    private static boolean isValidInteger(int num) {
        return num == (int) num;
    }

    /**
     * Filters out non-recurrent elements from the count map
     * @param countMap Map of element counts
     * @return Map of recurrent elements and their counts
     */
    private static Map<Integer, Integer> filterRecurrentEntities(Map<Integer, Integer> countMap) {
        Map<Integer, Integer> recurrentMap = new HashMap<>();

        for (Map.Entry<Integer, Integer> entry : countMap.entrySet()) {
            if (entry.getValue() > 1) {
                recurrentMap.put(entry.getKey(), entry.getValue());
            }
        }

        return recurrentMap;
    }

    /**
     * Displays recurrent entities and their counts
     * @param recurrentEntities Map of recurrent elements and counts
     */
    public static void displayRecurrentEntities(Map<Integer, Integer> recurrentEntities) {
        for (Map.Entry<Integer, Integer> entry : recurrentEntities.entrySet()) {
            System.out.printf("%d appears %d times%n", entry.getKey(), entry.getValue());
        }
    }
}
