class CarException extends Exception {
    public CarException(String message) {
        super(message);
    }
}

class Car {
    private String make;
    private String model;
    private int year;
    private double fuelEfficiency; // in miles per gallon
    private double distanceTravelled; // in miles

    public Car(String make, String model, int year, double fuelEfficiency) throws CarException {
        if (year < 1900 || year > java.util.Calendar.getInstance().get(java.util.Calendar.YEAR)) {
            throw new CarException("Year must be between 1900 and the current year.");
        }
        if (fuelEfficiency <= 0) {
            throw new CarException("Fuel efficiency must be a positive value.");
        }

        this.make = make;
        this.model = model;
        this.year = year;
        this.fuelEfficiency = fuelEfficiency;
        this.distanceTravelled = 0.0;
    }

    public void accelerate(double increase) {
        System.out.println("Accelerating by " + increase + " mph.");
    }

    public void decelerate(double decrease) {
        System.out.println("Decelerating by " + decrease + " mph.");
    }

    public void refuel(double fuelAmount) throws CarException {
        if (fuelAmount < 0) {
            throw new CarException("Fuel amount must not be negative.");
        }
        System.out.println("Refueling with " + fuelAmount + " gallons.");
    }

    public double calculateFuelConsumption() {
        return distanceTravelled / fuelEfficiency;
    }

    public void drive(double distance) throws CarException {
        if (distance < 0) {
            throw new CarException("Distance travelled must not be negative.");
        }
        distanceTravelled += distance;
        System.out.println("Driving " + distance + " miles.");
    }

    public static void main(String[] args) {
        try {
            Car myCar = new Car("Toyota", "Camry", 2020, 30);
            myCar.accelerate(60);
            myCar.drive(120);
            myCar.refuel(10);
            double fuelConsumed = myCar.calculateFuelConsumption();
            System.out.println("Fuel consumed: " + fuelConsumed + " gallons.");
        } catch (CarException e) {
            System.err.println("Error: " + e.getMessage());
        }
    }
}
