            fuelLevel += gallons;
            System.out.println("Refueled " + gallons + " gallons. Current fuel level: " + fuelLevel);
        }
    }

    public double calculateFuelConsumption() {
        return distanceTravelled / fuelEfficiency;
    }

    public void drive(double hours) {
        double distance = speed * hours;
        distanceTravelled += distance;
        double fuelUsed = distance / fuelEfficiency;
        fuelLevel -= fuelUsed;
