        System.out.println("Maximum TPDU size for valid input: " + calculateMaxTPDUSize(validMaxTPDUSizeParam));

        // Test the function with invalid input
        int invalidMaxTPDUSizeParam = 6;
        try {
            System.out.println("Maximum TPDU size for invalid input: " + calculateMaxTPDUSize(invalidMaxTPDUSizeParam));
        } catch (IllegalArgumentException e) {
            System.out.println("Exception caught: " + e.getMessage());
        }
    }
}
