import unittest
import random
import math

class EuclideanPoint:
    def __init__(self, x, y):
        self.x = x
        self.y = y

    def distance_to(self, other):
        return math.sqrt((self.x - other.x) ** 2 + (self.y - other.y) ** 2)

def rand(k, x_range, y_range):
    points = []
    for _ in range(k):
        x = random.randint(x_range[0], x_range[1])
        y = random.randint(y_range[0], y_range[1])
        points.append(EuclideanPoint(x, y))
    return points

class Line:
    def __init__(self, start, end):
        self.start = start
        self.end = end

    def length(self):
        return self.start.distance_to(self.end)

# Unit tests
class TestEuclideanPoint(unittest.TestCase):
    def test_distance_to(self):
        p1 = EuclideanPoint(1, 2)
        p2 = EuclideanPoint(4, 6)
        self.assertAlmostEqual(p1.distance_to(p2), 5.0, places=5)

class TestRand(unittest.TestCase):
    def test_rand(self):
        points = rand(5, (0, 10), (0, 10))
        self.assertEqual(len(points), 5)
        for point in points:
            self.assertTrue(0 <= point.x <= 10)
            self.assertTrue(0 <= point.y <= 10)

class TestLine(unittest.TestCase):
    def test_length(self):
        p1 = EuclideanPoint(1, 2)
        p2 = EuclideanPoint(4, 6)
        line = Line(p1, p2)
        self.assertAlmostEqual(line.length(), 5.0, places=5)

if __name__ == '__main__':
    unittest.main()
```

