import numpy as np

def local_extreme(arr, comp_func, axis, order):
    if axis == 0:
        arr = arr.T
    mask = np.full_like(arr, True, dtype=bool)
    for i in range(1, arr.shape[0] - 1):
        if comp_func(arr[i], arr[i - 1]) and comp_func(arr[i], arr[i + 1]):
            mask[i] = False
    if order == 1:
        return mask
    else:
        for _ in range(order - 1):
            arr[mask] = False
            for i in range(1, arr.shape[0] - 1):
                if comp_func(arr[i], arr[i - 1]) and comp_func(arr[i], arr[i + 1]):
                    mask[i] = False
        return mask if axis == 0 else mask.T

def ridge_detection(local_max, row, col, n_rows, n_cols, up, down):
    rows = [row]
    cols = [col]
    while True:
        if up and row > 0 and local_max[row - 1, col]:
            row -= 1
            rows.append(row)
        elif down and row < n_rows - 1 and local_max[row + 1, col]:
            row += 1
            rows.append(row)
        else:
            break
    return rows, cols
```
