            account_holder = input("Enter account holder name: ")
            accounts[account_holder] = BankAccount(account_holder)
            print(f"Account created for {account_holder}")

        elif choice == '2':
            account_holder = input("Enter account holder name: ")
            if account_holder in accounts:
                amount = float(input("Enter deposit amount: "))
                accounts[account_holder].deposit(amount)
            else:
                print("Account not found")

        elif choice == '3':
            account_holder = input("Enter account holder name: ")
            if account_holder in accounts:
                amount = float(input("Enter withdrawal amount: "))
                accounts[account_holder].withdraw(amount)
            else:
                print("Account not found")

        elif choice == '4':
            account_holder = input("Enter account holder name: ")
            if account_holder in accounts:
                accounts[account_holder].check_balance()
            else:
                print("Account not found")

        elif choice == '5':
