        print("Available Books:")
        for book in self.books:
            if book not in self.borrowed_books.values():
                print(book)

    def borrow_book(self, user, book):
        if book in self.books and book not in self.borrowed_books.values():
            self.borrowed_books[user] = book
            print(f"{user} has successfully borrowed {book}.")
