Design a C++ class for a doubly-linked list with a self-adjusting feature, where the most recently searched node moves to the head of the list to optimize future search operations. Implement node deletion function as well.
.dll

```
#include<iostream>
using namespace std;

struct Node {
    int data;
    Node* prev;
    Node* next;
};

class DoublyLinkedList {
private:
    Node* head;
public:
    DoublyLinkedList() : head(nullptr) {}

    // Function to insert at the beginning
    void insert(int value) {
        Node* newNode = new Node{value, nullptr, head};
        if (head != nullptr)
            head->prev = newNode;
        head = newNode;
    }

    // Function to delete a node by its value
    bool remove(int value) {
        Node* current = head;
        while (current != nullptr) {
            if (current->data == value) {
                if (current->prev != nullptr)
                    current->prev->next = current->next;
                else
                    head = current->next;
                if (current->next != nullptr)
                    current->next->prev = current->prev;
                delete current;
                return true;
            }
            current = current->next;
        }
        return false;
    }

    // Function to move the most recently accessed node to the front
    void moveToHead(Node* node) {
        if (node == head || node == nullptr)
            return;

        node->prev->next = node->next;
        if (node->next != nullptr)
            node->next->prev = node->prev;

        node->next = head;
        node->prev = nullptr;
        head->prev = node;
        head = node;
    }

    // Function to display the list
    void display() {
        Node* temp = head;
        while (temp != nullptr) {
            cout << temp->data << " ";
            temp = temp->next;
        }
        cout << endl;
    }
};

int main() {
    DoublyLinkedList dll;
    dll.insert(10);
    dll.insert(20);
    dll.insert(30);

    cout << "Original List: ";
    dll.display();

    dll.moveToHead(dll.head->next);  // Move 20 to the front
    cout << "List after moving 20 to the front: ";
    dll.display();

    dll.remove(20);
    cout << "List after removing 20: ";
    dll.display();

    return 0;
}
