:
```python
import muttlib.dbconn.SqlServerClient

class DatabaseManager:
    def __init__(self, server, database, username, password):
        self.client = muttlib.dbconn.SqlServerClient(server, database, username, password)

    def create_record(self, name, age):
        try:
            query = "INSERT INTO my_table (name, age) VALUES (%s, %s)"
            params = (name, age)
            result = self.client.execute_query(query, params)
            if result:
                print("Record created successfully")
            else:
                print("Failed to create record")
        except Exception as e:
            print(f"Error creating record: {e}")

    def read_record(self, record_id):
        try:
            query = "SELECT * FROM my_table WHERE id = %s"
            params = (record_id,)
