```python
import sqlite3
filename = "mydb.sqlite"
table_name = "mytable"

def create_connection(db_file):
    conn = None;
    try:
        conn = sqlite3.connect(db_file)
    except Exception as error:
        print(error)

    if conn:
        return conn 
    else:
        return None

def select_all_from_table(conn, table):
    try:
        cur = conn.cursor()
        cur.execute("SELECT * FROM ?", [table])

        rows = cur.fetchall()

        for row in rows:
            print(row)
    except Exception as error:
        print(error)

conn = create_connection(filename)

if conn is not None
    select_all_from_table(conn, table_name)
else:
