// Function to check if a single string has balanced parentheses
bool is_balanced(const string &s) {
    int balance = 0;
    for (char c : s) {
        if (c == '(') {
            balance++;
        } else if (c == ')') {
            balance--;
        }
        // If balance goes negative at any point, it's unbalanced
        if (balance < 0) {
            return false;
        }
    }
    // Only balanced if we end with 0
    return balance == 0;
}

