    if (is_balanced(combined1)) {
        return "Yes";
    }

    // Check second combination (s2 + s1)
    string combined2 = lst[1] + lst[0];
    if (is_balanced(combined2)) {
        return "Yes";
    }

    // If neither combination is balanced
    return "No";
}

