/**
 * Function to calculate the first n Fibonacci numbers.
 * 
 * @param n The number of Fibonacci numbers to generate.
 * @return A vector containing the first n Fibonacci numbers.
 */
std::vector<int> calculateFibonacci(int n) {
    std::vector<int> fibonacciNumbers;

    // Handle cases where n is less than or equal to 0
    if (n <= 0) {
        return fibonacciNumbers; // return an empty vector
    }

    // Initialize the first two Fibonacci numbers
    fibonacciNumbers.push_back(0); // F(0)
    if (n > 1) {
        fibonacciNumbers.push_back(1); // F(1)
    }

    // Calculate Fibonacci numbers from F(2) to F(n-1)
    for (int i = 2; i < n; ++i) {
        int nextFibonacci = fibonacciNumbers[i - 1] + fibonacciNumbers[i - 2];
        fibonacciNumbers.push_back(nextFibonacci);
    }

    return fibonacciNumbers;
}

int main() {
