    // Ask user for input
    std::cout << "Enter an integer to calculate the first n Fibonacci numbers: ";
    std::cin >> n;

    // Get the Fibonacci numbers
    std::vector<int> fibonacciNumbers = calculateFibonacci(n);

    // Print the Fibonacci numbers
    std::cout << "The first " << n << " Fibonacci numbers are: ";
    for (size_t i = 0; i < fibonacciNumbers.size(); ++i) {
        std::cout << fibonacciNumbers[i] << (i < fibonacciNumbers.size() - 1 ? ", " : "");
    }
    std::cout << std::endl;

    return 0;
}
