/**
 * @brief Generates the first n Fibonacci numbers.
 * 
 * The Fibonacci sequence is a series of numbers where each number is the sum of
 * the two preceding ones, starting from 0 and 1. This function handles edge cases
 * for n <= 0 and n == 1 appropriately.
 * 
 * @param n The number of Fibonacci numbers to generate (must be non-negative).
 * @return std::vector<unsigned long long> A vector containing the first n Fibonacci numbers.
 */
std::vector<unsigned long long> generateFibonacciNumbers(int n) {
    std::vector<unsigned long long> fibNumbers;

    // Handle edge cases
    if (n <= 0) {
        return fibNumbers;  // Return empty vector for invalid input
    }

    // First Fibonacci number is always 0
    fibNumbers.push_back(0);
    if (n == 1) {
        return fibNumbers;
    }

    // Second Fibonacci number is always 1
    fibNumbers.push_back(1);
    if (n == 2) {
        return fibNumbers;
    }

    // Generate subsequent Fibonacci numbers
    for (int i = 2; i < n; ++i) {
        // Each number is the sum of the two preceding numbers
        unsigned long long nextFib = fibNumbers[i-1] + fibNumbers[i-2];
        fibNumbers.push_back(nextFib);
    }

    return fibNumbers;
}

