```cpp
/*
 * Simple SFML Server-Client Communication System
 * 
 * This program demonstrates basic TCP communication between a server and client.
 * The server listens for incoming connections on port 55001 and sends a welcome
 * message to any connected client. The client connects to the server and displays
 * the received message.
 */

#include <SFML/Network.hpp>
#include <iostream>
#include <thread>

void runServer()
{
    /*
     * Server Functionality:
     * 1. Creates a TCP listener on port 55001
     * 2. Continuously accepts incoming connections
     * 3. Sends a welcome message to each connected client
     */

    // Create and bind the listener to port 55001
    sf::TcpListener listener;
    if (listener.listen(55001) != sf::Socket::Done)
    {
        std::cerr << "Server: Failed to bind to port 55001" << std::endl;
        return;
    }
    std::cout << "Server: Listening on port 55001..." << std::endl;

    // Main server loop
    while (true)
    {
        // Accept new client connections
        sf::TcpSocket client;
        if (listener.accept(client) == sf::Socket::Done)
        {
            std::cout << "Server: New client connected from "
                      << client.getRemoteAddress() << std::endl;

            // Prepare and send welcome message
            std::string message = "Welcome to the server!";
            if (client.send(message.c_str(), message.size() + 1) != sf::Socket::Done)
            {
                std::cerr << "Server: Failed to send welcome message" << std::endl;
            }
            else
            {
                std::cout << "Server: Sent welcome message to client" << std::endl;
            }
        }
    }
}

void runClient()
{
    /*
     * Client Functionality:
     * 1. Connects to the server at 127.0.0.1:55001
     * 2. Receives and displays the welcome message
     */

    // Create socket and connect to server
    sf::TcpSocket socket;
    std::cout << "Client: Attempting to connect to server..." << std::endl;

    if (socket.connect("127.0.0.1", 55001) == sf::Socket::Done)
    {
        std::cout << "Client: Successfully connected to server" << std::endl;

        // Receive message from server
        char buffer[100];
        std::size_t received;
        if (socket.receive(buffer, sizeof(buffer), received) == sf::Socket::Done)
        {
            std::cout << "Client: Received message - " << buffer << std::endl;
        }
        else
        {
            std::cerr << "Client: Failed to receive message" << std::endl;
        }
    }
    else
    {
        std::cerr << "Client: Failed to connect to server" << std::endl;
    }
}

int main()
{
    // Start server in a separate thread
    std::thread serverThread(runServer);

    // Give server time to start
    std::this_thread::sleep_for(std::chrono::milliseconds(100));

    // Run client
    runClient();

    // Wait for server thread to finish (though it runs indefinitely)
    serverThread.join();

    return 0;
}
```

