bool is_balanced(const string &s) {
    int balance = 0;
    for (char c : s) {
        if (c == '(') {
            balance++;
        } else if (c == ')') {
            balance--;
        }
        // If balance goes negative at any point, it's unbalanced
        if (balance < 0) {
