```cpp
#include <SFML/Network.hpp>
#include <iostream>
#include <thread>

/**
 * @brief Server application to handle client connections and send welcome messages
 * 
 * This function creates a TCP listener that accepts incoming client connections
 * and sends a welcome message to each connected client.
 */
void runServer() {
    // Create a TCP listener socket bound to port 55001
    sf::TcpListener listener;

    // Attempt to listen on the specified port
    if (listener.listen(55001) != sf::Socket::Done) {
        std::cerr << "Failed to listen on port 55001" << std::endl;
        return;
    }

    std::cout << "Server is listening on port 55001..." << std::endl;

    // Continuous loop to accept client connections
    while (true) {
        // Create a socket to represent the connecting client
        sf::TcpSocket client;

        // Wait for and accept an incoming client connection
        if (listener.accept(client) == sf::Socket::Done) {
            // Successfully connected client
            std::cout << "Client connected from: " 
                      << client.getRemoteAddress().toString() << std::endl;

            // Prepare welcome message
            std::string message = "Welcome to the server!";

            // Send welcome message to the connected client
            if (client.send(message.c_str(), message.length() + 1) != sf::Socket::Done) {
                std::cerr << "Failed to send welcome message" << std::endl;
            }
        }
    }
}

/**
 * @brief Client application to connect to server and receive welcome message
 * 
 * This function attempts to connect to the server, receive a welcome message,
 * and display it.
 */
void runClient() {
    // Create a TCP socket for client connection
    sf::TcpSocket socket;

    // Attempt to connect to localhost on port 55001
    if (socket.connect("127.0.0.1", 55001) != sf::Socket::Done) {
        std::cerr << "Failed to connect to server" << std::endl;
        return;
    }

    std::cout << "Connected to server" << std::endl;

    // Buffer to store received message
    char buffer[100];

    // Variable to store number of bytes received
    std::size_t received;

    // Receive message from server
    if (socket.receive(buffer, sizeof(buffer), received) == sf::Socket::Done) {
        // Display received message
        std::cout << "Received from server: " << buffer << std::endl;
    }
}

/**
 * @brief Main application entry point
 * 
 * Demonstrates server-client communication by running server and client
 * in separate threads.
 * 
 * @return int Application exit status
 */
int main() {
    try {
        // Create threads for server and client
        std::thread serverThread(runServer);
        std::thread clientThread(runClient);

        // Wait for both threads to complete
        serverThread.join();
        clientThread.join();
    }
    catch (const std::exception& e) {
        std::cerr << "Exception occurred: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
```

