import java.util.Arrays;
import java.util.HashMap;

public static String[] findAnagrams(String[] lexemes) {
    HashMap<String, ArrayList<String>> anagramGroups = new HashMap<>();

    for (String lexeme : lexemes) {
        if (lexeme.length() >= 2) {
            char[] chars = lexeme.toCharArray();
            Arrays.sort(chars);
            String sortedLexeme = new String(chars);

            anagramGroups.computeIfAbsent(sortedLexeme, k -> new ArrayList<>()).add(lexeme);
        }
    }

    ArrayList<String> resultantArray = new ArrayList<>();
    for (ArrayList<String> group : anagramGroups.values()) {
        if (group.size() > 1) {
            resultantArray.addAll(group);
        }
    }

    return resultantArray.toArray(new String[0]);
}
```

