class Solution:
    def braceExpansionII(self, expression: str) -> list[str]:
        groups = [[]]
        level = [0]

        for i, c in enumerate(expression):
            if c == '{':
                level.append(i)
                groups.append([])
            elif c == '}':
                level.pop()

                if i - 1 in level or i + 1 == len(expression) or expression[i + 1] == ',':
                    vals = set(groups.pop())

                    for _ in range(len(groups[-1]) - 1, -1, -1):
                        if not groups[-1][_]:
                            groups[-1].pop()
                        elif groups[-1][_][-1] == ',':
                            groups[-1][_] = vals
                            break
                        else:
                            groups[-1][_] = {a + b for a in groups[-1][_] for b in vals}

            elif c == ',':
                groups[-1].append(',')
            else:
                if not groups[-1] or groups[-1][-1] == ',':
                    groups[-1].append(set([c]))
                else:
                    groups[-1][-1] = {a + c for a in groups[-1][-1]}

        return sorted(next(iter(groups[-1])))
