        super().__init__("Lion", "Savanna", "Carnivore", 12)
        self._pride_size = pride_size
        self._territory = territory

    def hunt(self):
        return f"Lion hunting in {self._territory} with pride of {self._pride_size}"

class Elephant(Animal):
    def __init__(self, herd_size, water_source):
        super().__init__("Elephant", "Grasslands", "Herbivore", 60)
        self._herd_size = herd_size
        self._water_source = water_source

    def trumpet(self):
        return f"Elephant trumpeting near {self._water_source}"

class Habitat:
    def __init__(self, name):
        self._name = name
        self._animals = []

    def add_animal(self, animal):
        if not isinstance(animal, Animal):
            raise ValueError("Only Animal instances can be added")
        self._animals.append(animal)

    def remove_animal(self, animal):
        if animal in self._animals:
            self._animals.remove(animal)
        else:
            raise ValueError("Animal not found in habitat")

    def count_animals(self):
        return len(self._animals)

    def average_lifespan(self):
        if not self._animals:
            return 0
        return sum(animal.get_lifespan() for animal in self._animals) / len(self._animals)

def main():
