```cpp
#include <iostream>
#include <string>
#include <vector>
#include <numeric>

class StudentPortfolio {
private:
    std::string fullName;
    std::string academicID;
    std::vector<double> grades;

public:
    // Constructor to initialize the portfolio
    StudentPortfolio(const std::string& name, const std::string& id)
        : fullName(name), academicID(id) {}

    // Method to add a grade
    void addGrade(double grade) {
        if (grade >= 0.0 && grade <= 4.0) { // GPA typically ranges from 0.0 to 4.0
            grades.push_back(grade);
        } else {
            std::cerr << "Invalid grade. Please enter a value between 0.0 and 4.0." << std::endl;
        }
    }

    // Method to calculate GPA
    double calculateGPA() const {
        if (grades.empty()) return 0.0; // Avoid division by zero
        double total = std::accumulate(grades.begin(), grades.end(), 0.0);
        return total / grades.size();
    }

    // Method to display portfolio details
    void displayPortfolio() const {
        std::cout << "Student Name: " << fullName << std::endl;
        std::cout << "Academic ID: " << academicID << std::endl;
        std::cout << "Current GPA: " << calculateGPA() << std::endl;
    }
};

int main() {
    StudentPortfolio student("John Doe", "A00123456");

    student.addGrade(3.5);
    student.addGrade(4.0);
    student.addGrade(2.7);
    student.addGrade(3.3);

    student.displayPortfolio();

    return 0;
}
```

