def validate_input(value, conversion_type):
    if conversion_type == 'int_to_roman':
        if not isinstance(value, int) or not (1 <= value <= 1_000_000):
            raise ValueError("Integer must be in the range 1 to 1,000,000.")
    elif conversion_type == 'roman_to_int':
