            raise ValueError("Invalid Roman numeral.")

def format_roman_output(roman, format_output):
    if not format_output:
        return roman
    groups = []
    while roman:
        groups.append(roman[:5])
        roman = roman[5:]
    return ''.join(f'({group})' for group in groups)

def int_to_roman(number, include_subtractive):
    val = [
        1000000, 900000, 500000, 400000, 100000,
        90000, 50000, 40000, 10000, 9000,
        5000, 4000, 1000, 900, 500, 400,
        100, 90, 50, 40, 10,
        9, 5, 4, 1
    ]
    syms = [
        "M", "CM", "D", "CD", "C", 
        "XC", "L", "XL", "X", "MX",
        "V", "IV", "M", "CM", "D", "CD",
        "C", "XC", "L", "XL", "X",
        "IX", "V", "IV", "I"
    ]

    roman_numeral = ""
    for i in range(len(val)):
        while number >= val[i]:
            roman_numeral += syms[i]
            number -= val[i]

    if not include_subtractive:
        roman_numeral = roman_numeral.replace("IV", "IIII").replace("IX", "VIIII").replace("XL", "XXXX").replace("XC", "LXXXX")

    return format_roman_output(roman_numeral, True)

def roman_to_int(roman):
    roman_numerals = {
        'i': 1, 'v': 5, 'x': 10, 'l': 50,
        'c': 100, 'd': 500, 'm': 1000
    }
    total = 0
    prev_value = 0

    for char in reversed(roman):
        value = roman_numerals[char]
        if value < prev_value:
            total -= value
        else:
            total += value
        prev_value = value

    return total

def advanced_roman_numeral_conversion(number, conversion_type, include_subtractive, validate, format_output):
    if validate:
        validate_input(number, conversion_type)

