Here's a complete and executable solution that implements the server-client communication using SFML:

```cpp
#include <SFML/Network.hpp>
#include <iostream>
#include <thread>

// Server function
void runServer() {
    // Create a TCP listener to accept incoming connections
    sf::TcpListener listener;

    // Bind the listener to a port
    if (listener.listen(55001) != sf::Socket::Done) {
        std::cerr << "Could not listen to port 55001" << std::endl;
        return;
    }

    std::cout << "Server is listening on port 55001..." << std::endl;

    // Endless loop to handle client connections
    while (true) {
        // Create a socket for the client
        sf::TcpSocket client;

        // Wait for a client connection
        if (listener.accept(client) == sf::Socket::Done) {
            std::cout << "New client connected!" << std::endl;

            // Prepare welcome message
            std::string message = "Welcome to the server!";

            // Send the message to the client
            if (client.send(message.c_str(), message.length() + 1) != sf::Socket::Done) {
                std::cerr << "Error sending message to client" << std::endl;
            }

            // Close the client connection
            client.disconnect();
        }
    }
}

// Client function
void runClient() {
    // Create a TCP socket
    sf::TcpSocket socket;

    // Connect to the server
    if (socket.connect("127.0.0.1", 55001) != sf::Socket::Done) {
        std::cerr << "Could not connect to the server" << std::endl;
        return;
    }

    std::cout << "Connected to server" << std::endl;

    // Buffer to receive message
    char buffer[100];
    std::size_t received;

    // Receive message from server
    if (socket.receive(buffer, sizeof(buffer), received) == sf::Socket::Done) {
        std::cout << "Received: " << buffer << std::endl;
    }

    // Close the socket
    socket.disconnect();
}

int main() {
    // Create threads for server and client
    std::thread serverThread(runServer);
    std::this_thread::sleep_for(std::chrono::seconds(1)); // Give server time to start
    std::thread clientThread(runClient);

    // Wait for both threads to complete
    serverThread.join();
    clientThread.join();

    return 0;
}
```

