/**
 * Calculates the standard deviation of a given array of float values.
 * 
 * @param data Array of float values to compute standard deviation
 * @return Standard deviation of the input array, or Double.NaN if array is empty
 * @throws IllegalArgumentException if input array is null
 */
public static double calculateStandardDeviation(float[] data) {
    // Validate input array
    if (data == null) {
        throw new IllegalArgumentException("Input array cannot be null");
    }

    // Handle empty array case
    if (data.length == 0) {
        return Double.NaN;
    }

    // Calculate mean (average)
    double sum = 0.0;
    for (float value : data) {
        sum += value;
    }
    double mean = sum / data.length;

    // Calculate variance (squared differences from mean)
    double sumSquaredDiff = 0.0;
    for (float value : data) {
        double diff = value - mean;
        sumSquaredDiff += diff * diff;
    }

    // Calculate standard deviation (square root of variance)
    double variance = sumSquaredDiff / data.length;
    return Math.sqrt(variance);
}

// Example usage demonstration
