```java
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.HashMap;
import java.util.Map;
import java.util.stream.Stream;

public class IOUtils {

    public static Map<String, Path> getAllFiles(String directoryPath) {
        Map<String, Path> fileMap = new HashMap<>();
        Path startPath = Paths.get(directoryPath);

        try (Stream<Path> paths = Files.walk(startPath)) {
            paths.filter(Files::isRegularFile)
                 .forEach(file -> {
                     String relativePath = startPath.relativize(file).toString();
                     fileMap.put(relativePath, file.toAbsolutePath());
                 });
        } catch (IOException e) {
            // Handle the exception appropriately
            LOG.error("An error occurred while retrieving files: {}", e.getMessage(), e);
        }

        return fileMap;
    }
}
```

