    public List<String> getNames() {
        JSONParser parser = new JSONParser();
        try {
            Object obj = parser.parse(new FileReader("mypath/name.json"));

            if (obj instanceof JSONObject) {
                // Handle JSON object case (even with duplicate keys)
                JSONObject jsonObject = (JSONObject) obj;
                for (Object value : jsonObject.values()) {
                    if (value instanceof String) {
                        datasource.add((String) value);
                    }
                }
            } else if (obj instanceof JSONArray) {
                // Handle JSON array case
                JSONArray jsonArray = (JSONArray) obj;
                for (Object item : jsonArray) {
                    if (item instanceof String) {
                        datasource.add((String) item);
                    }
                }
            }
        } catch (IOException | ParseException e) {
            e.printStackTrace();
        }
        return datasource;
    }

    public static void main(String[] args) {
        NameReader reader = new NameReader();
        List<String> names = reader.getNames();
        System.out.println(names);
    }
}
