/*
 * Simple Inventory Management System
 * Author: Your Name
 * License: MIT
 */

#include <iostream>
#include <string>

class Product {
private:
    std::string name;
    int quantity;

public:
    // Constructor
    Product(const std::string& productName, int initialQuantity) 
        : name(productName), quantity(initialQuantity) {}

    // Add stock to the product
    void addStock(int amount) {
        if (amount > 0) {
            quantity += amount;
            std::cout << "Added " << amount << " units of " << name << ". New stock: " << quantity << std::endl;
        } else {
            std::cout << "Invalid amount. Please add a positive number." << std::endl;
        }
    }

    // Sell the product
    void sell(int amount) {
        if (amount <= 0) {
            std::cout << "Invalid amount. Please sell a positive number." << std::endl;
        } else if (amount > quantity) {
            std::cout << "Cannot sell " << amount << " units of " << name << ". Insufficient stock (available: " << quantity << ")." << std::endl;
        } else {
            quantity -= amount;
            std::cout << "Sold " << amount << " units of " << name << ". Remaining stock: " << quantity << std::endl;
        }
    }

    // Getter for product name (optional, for demonstration)
    std::string getName() const {
        return name;
    }

    // Getter for quantity (optional, for demonstration)
    int getQuantity() const {
        return quantity;
    }
};

int main() {
    // Create some products
    Product apple("Apple", 10);
    Product banana("Banana", 5);

    // Demonstrate adding stock
    apple.addStock(5);   // Adds 5 apples
    banana.addStock(3);  // Adds 3 bananas

    // Demonstrate selling products
    apple.sell(8);       // Sells 8 apples
    banana.sell(10);     // Attempts to sell 10 bananas (insufficient stock)
    apple.sell(10);      // Sells 10 apples (now out of stock)
    banana.sell(2);      // Sells 2 bananas

    return 0;
}
```

