#include <stdint.h>

// ADC configuration structure
typedef struct {
    uint8_t reference_voltage;  // 0: VREF=AVCC, 1: Internal 1.1V, 2: External AREF
    uint8_t clock_prescaler;    // Clock divider (2,4,8,16,32,64,128)
    uint8_t resolution;         // 8, 10, or 12 bits
} AdcConfig;

// Initialize ADC with given configuration
void adc_init(AdcConfig config);

// Read analog value from specified channel (0-15 typically)
uint16_t adc_read_channel(uint8_t channel);

// Read and average multiple samples from a channel
uint16_t adc_read_average(uint8_t channel, uint8_t samples);

// Convert raw ADC value to millivolts
uint16_t adc_to_millivolts(uint16_t raw_value, uint16_t max_voltage);

#endif // ADC_H
```

```c
// adc.c

#include "adc.h"
#include <avr/io.h>

static AdcConfig current_config;

void adc_init(AdcConfig config) {
    current_config = config;

    // Set reference voltage
    ADMUX = (ADMUX & 0x3F) | ((config.reference_voltage & 0x03) << 6);

    // Set ADC enable and prescaler
    ADCSRA = (1 << ADEN) | ((config.clock_prescaler >> 1) & 0x07);

    // For microcontrollers that support different resolutions
    if (config.resolution == 8) {
        ADMUX |= (1 << ADLAR);  // Left adjust for 8-bit result
    }
}

uint16_t adc_read_channel(uint8_t channel) {
    // Validate channel number
    if (channel > 15) return 0;

    // Select channel
    ADMUX = (ADMUX & 0xF0) | (channel & 0x0F);

    // Start conversion
    ADCSRA |= (1 << ADSC);

    // Wait for conversion to complete
    while (ADCSRA & (1 << ADSC));

    // Return appropriate resolution
    if (current_config.resolution == 8) {
        return ADCH;  // 8-bit result
    } else {
        return ADC;   // 10-bit result (or higher)
    }
}

uint16_t adc_read_average(uint8_t channel, uint8_t samples) {
    uint32_t sum = 0;

    for (uint8_t i = 0; i < samples; i++) {
        sum += adc_read_channel(channel);
    }

    return (uint16_t)(sum / samples);
}

uint16_t adc_to_millivolts(uint16_t raw_value, uint16_t max_voltage) {
    uint32_t max_adc = (1 << current_config.resolution) - 1;
    return (uint16_t)((raw_value * (uint32_t)max_voltage) / max_adc);
}
```

