import java.net.URL;
import java.io.InputStream;
import java.io.BufferedReader;
import java.io.InputStreamReader;

public class ConfigurationReader {
    private final ConfigurationFormat configFormat;
    private final URL resource;

    public ConfigurationReader(ConfigurationFormat format, URL resource) {
        this.configFormat = format;
        this.resource = resource;
    }

    public String readConfiguration() throws Exception {
        StringBuilder configurationData = new StringBuilder();
        try (InputStream inputStream = resource.openStream();
             BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream))) {
            String line;
            while ((line = reader.readLine()) != null) {
                configurationData.append(line).append("\n");
            }
        } catch (Exception e) {
            throw new Exception("Error reading configuration", e);
        }
        return configurationData.toString();
    }
}
