public class DependencyManager {
    private WatchService watchService;

    public DependencyManager() throws IOException {
        this.watchService = FileSystems.getDefault().newWatchService();
    }

    public void addWatchPath(String path) throws IOException {
        Paths.get(path).register(watchService,
                StandardWatchEventKinds.ENTRY_CREATE,
                StandardWatchEventKinds.ENTRY_DELETE,
                StandardWatchEventKinds.ENTRY_MODIFY);
    }

    public boolean waitReload(Dependency dep, long timeout) {
        try {
            long startTime = System.currentTimeMillis();
            while (System.currentTimeMillis() - startTime < timeout) {
                WatchKey key = watchService.poll(timeout - (System.currentTimeMillis() - startTime), TimeUnit.MILLISECONDS);
                if (key != null) {
                    for (WatchEvent<?> event : key.pollEvents()) {
                        Kind<?> kind = event.kind();
                        if (kind == StandardWatchEventKinds.ENTRY_CREATE ||
                                kind == StandardWatchEventKinds.ENTRY_DELETE ||
                                kind == StandardWatchEventKinds.ENTRY_MODIFY) {
                            // Perform reload operation
                            dep.reload();
                            return true;
                        }
                    }
                    key.reset();
                } else {
                    break; // Timeout occurred
                }
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
        return false; // No changes detected within the timeout period
    }
}
