def cache_etag_last_modified(view_method, etag_func=None, last_modified_func=None):
    @wraps(view_method)
    def wrapper(request, *args, **kwargs):
        # Generate ETag and Last-Modified values using the provided functions
        etag = etag_func() if etag_func else None
        last_modified = last_modified_func() if last_modified_func else None

        # Check if client has sent If-None-Match or If-Modified-Since header
        if_none_match = request.headers.get('If-None-Match')
        if_modified_since = request.headers.get('If-Modified-Since')

        # Compare ETag and Last-Modified values with those sent by the client
        if if_none_match == etag or (if_modified_since and datetime.datetime.strptime(if_modified_since, '%a, %d %b %Y %H:%M:%S GMT') < last_modified):
            # Return 304 Not Modified response if cached version is up-to-date
            return HttpResponseNotModified()

        # Call the original view method to generate the response
        response = view_method(request, *args, **kwargs)

        # Add ETag and Last-Modified headers to the response
        if etag:
            response['ETag'] = etag
        if last_modified:
            response['Last-Modified'] = last_modified.strftime('%a, %d %b %Y %H:%M:%S GMT')

        return response

    return wrapper


