function getPrimes(nums) {
    // An empty array to store the prime numbers
    let primes = [];

    // Iterate over the given numbers
    for (let num of nums) {
        // Prime numbers are greater than 1
        if (num > 1) {
            let isPrime = true;  // Assume the number is prime until proven it's not

            /* The sqrt(num) optimization isn't included in this JavaScript translation
             * because JavaScript does not have built-in integer square root function.
             * So we'll just check all numbers from 2 up to (num - 1).
             */
