from django.shortcuts import render
from django.http import JsonResponse
from django.views import View
from .models import User
from django.contrib.auth import authenticate, login, logout

class UserView(View):
    """
    UserView handles the creation of new users and user authentication.
    """

    def post(self, request):
        """
        Handle POST requests.
        """
        data = request.POST
        username = data.get('username')
        password = data.get('password')
        email = data.get('email')
        interests = data.get('interests')

        if not all([username, password, email, interests]):
            return JsonResponse({'error': 'Missing required information.'}, status=400)

        user = User.objects.create_user(username=username, password=password, email=email, interests=interests)
        user.save()

        return JsonResponse({'message': 'User created successfully.'}, status=201)

    def get(self, request):
        """
        Handle GET requests.
        """
        username = request.GET.get('username')
        password = request.GET.get('password')

        if not all([username, password]):
            return JsonResponse({'error': 'Missing required information.'}, status=400)

        user = authenticate(request, username=username, password=password)

        if user is not None:
            login(request, user)
            return JsonResponse({'message': 'Login successful.'}, status=200)
        else:
            return JsonResponse({'error': 'Invalid login credentials.'}, status=401)

    def delete(self, request):
        """
        Handle DELETE requests.
        """
        logout(request)
        return JsonResponse({'message': 'Logout successful.'}, status=200)
```

