            del self.patrons[patron_id]
        else:
            print("Patron not found.")

    def search_patron(self, patron_id=None, name=None):
        if patron_id:
            if patron_id in self.patrons:
                return self.patrons[patron_id]
            else:
                return "Patron not found."
        elif name:
            for patron in self.patrons.values():
                if patron["name"] == name:
                    return patron
            return "Patron not found."
        else:
            return "Please provide patron id or name."

    def borrow_book(self, patron_id, book_title, borrow_date):
        self.patrons[patron_id]["borrowed_books"][book_title] = {"borrow_date": borrow_date, "return_date": None}

    def return_book(self, patron_id, book_title, return_date):
        if book_title in self.patrons[patron_id]["borrowed_books"]:
            self.patrons[patron_id]["borrowed_books"][book_title]["return_date"] = return_date

    def check_overdue_books(self, patron_id):
        overdue_books = []
        for book, dates in self.patrons[patron_id]["borrowed_books"].items():
            if dates["return_date"] is None:
                overdue_books.append(book)
            else:
                borrow_date = datetime.strptime(dates["borrow_date"], "%Y-%m-%d")
                return_date = datetime.strptime(dates["return_date"], "%Y-%m-%d")
                if return_date < borrow_date:
                    overdue_books.append(book)
        return overdue_books

    def calculate_fines(self, patron_id):
        fines = 0
        for book, dates in self.patrons[patron_id]["borrowed_books"].items():
            if dates["return_date"] is not None:
                borrow_date = datetime.strptime(dates["borrow_date"], "%Y-%m-%d")
                return_date = datetime.strptime(dates["return_date"], "%Y-%m-%d")
                overdue_days = (return_date - borrow_date).days
                if overdue_days > 0:
                    fines += overdue_days
        return fines
