from typing import List, Dict
from database import DatabaseManager

class User:
    def __init__(self, id: int, username: str, password: str, db_manager: DatabaseManager):
        self.id = id
        self.username = username
        self.password = password
        self.interests = []
        self.reading_habits = {}
