        if interest not in self.interests:
            self.interests.append(interest)
            self.db_manager.update_user({
                'id': self.id,
                'username': self.username,
                'password': self.password,
                'interests': ','.join(self.interests),
                'reading_habits': ','.join(f'{k}:{v}' for k, v in self.reading_habits.items())
            })

    def update_reading_habits(self, article: Dict[str, str]):
        for tag in article['tags']:
            if tag in self.reading_habits:
                self.reading_habits[tag] += 1
            else:
                self.reading_habits[tag] = 1
        self.db_manager.update_user({
            'id': self.id,
            'username': self.username,
            'password': self.password,
            'interests': ','.join(self.interests),
            'reading_habits': ','.join(f'{k}:{v}' for k, v in self.reading_habits.items())
        })
