struct Node {
    int data;
    Node* prev;
    Node* next;
};

class List {
    private:
        Node* head;
        Node* tail;

    public:
        List() : head(nullptr), tail(nullptr) {}

        ~List() {
            while (head) {
                Node* old = head;
                head = head->next;
                delete old;
            }
        }

        void insert(int data) {
            Node* node = new Node;
            node->data = data;
            node->next = head;
            node->prev = nullptr;
            if (head != nullptr)
                head->prev = node;
            head = node;
            if (tail == nullptr)
                tail = node;
        }

        void search(int data) {
            Node* temp = head;
            while (temp) {
                if (temp->data == data)
                    break;
                temp = temp->next;
            }
            if (!temp) {
                cout << "No data " << data << " in list\n";
                return;
            }

            // Move searched node to head of list
            if (temp != head) {
                if (temp == tail) {
                    tail = tail->prev;
                    tail->next = nullptr;
                } else {
                    temp->next->prev = temp->prev;
                    temp->prev->next = temp->next;
                }
                temp->next = head;
                head->prev = temp;
                temp->prev = nullptr;
                head = temp;
            }
        }

        void deleteNode(int data) {
            Node* temp = head;
            while (temp) {
                if (temp->data == data)
                    break;
                temp = temp->next;
            }
            if (!temp) {
                cout << "No data " << data << " in list\n";
                return;
            }

            // Delete node from list
            if (temp == head) {
                head = head->next;
                if (head)
                    head->prev = nullptr;
                else
                    tail = nullptr;

            } else if (temp == tail) {
                tail = tail->prev;
                tail->next = nullptr;

            } else {
                temp->prev->next = temp->next;
                temp->next->prev = temp->prev;
            }
            delete temp;
        }

        void display () {
            Node* temp = head; 
            while (temp) {
                cout << temp->data << " ";
                temp = temp->next;
            }
            cout << "\n";
        }
};
```

