import ast

def extract_param_descriptions(func_str: str) -> dict:
    tree = ast.parse(func_str)
    func_def = next(node for node in ast.walk(tree) if isinstance(node, ast.FunctionDef))
    docstring = ast.get_docstring(func_def)

    param_descriptions = {}
    if docstring:
        for line in docstring.split('\n'):
            if line.strip().startswith('-'):
                param, desc = line.strip()[2:].split(':')
                param_descriptions[param.strip()] = desc.strip()

    return param_descriptions
