public class MultipleEventContainsFilter {
    private String[] eventContainsStrings;
    private boolean isAnd;
    private FilterFactory factory;

    public MultipleEventContainsFilter(String filterName, boolean isAnd, FilterFactory factory) {
        this.isAnd = isAnd;
        this.factory = factory;
    }

    public void setEventContainsString(String eventContainsString) {
        this.eventContainsStrings = eventContainsString.split(",");
    }

    public boolean passes(DefaultLogEvent event) {
        if (eventContainsStrings == null || eventContainsStrings.length == 0) {
            return false; // No strings to match
        }

        boolean eventMatches = false;
        for (String searchString : eventContainsStrings) {
            if (event.getMessage().contains(searchString)) {
                eventMatches = true;
                if (!isAnd) {
                    return true; // Match found and logic is OR
                }
            } else if (isAnd) {
                return false; // Match not found and logic is AND
            }
        }

        return eventMatches; // Return true if logic is AND and at least one match found
    }
}
