    list_stack.push(5)
    list_stack.push(10)
    list_stack.push(15)
    print(list_stack.peek())  # Output: 15
    print(list_stack.pop())   # Output: 15
    print(list_stack.peek())  # Output: 10
    print(list_stack.is_empty())  # Output: False
    print(list_stack.pop())   # Output: 10
    print(list_stack.pop())   # Output: 5
    print(list_stack.is_empty())  # Output: True
