from datetime import datetime

def longest_duration_sensor(log_file_path: str) -> str:
    sensor_data = {}

    with open(log_file_path, 'r') as file:
        for line in file:
            timestamp_str, sensor_info = line.split(']')[0][1:], line.split(']')[1]
            timestamp = datetime.strptime(timestamp_str, '%Y/%m/%d %H:%M:%S.%f')
            sensor_id = sensor_info.split(':')[0]

            if sensor_id not in sensor_data:
                sensor_data[sensor_id] = [timestamp, timestamp]
            else:
                if timestamp < sensor_data[sensor_id][0]:
                    sensor_data[sensor_id][0] = timestamp
                elif timestamp > sensor_data[sensor_id][1]:
                    sensor_data[sensor_id][1] = timestamp

    longest_duration = max((end - start for start, end in sensor_data.values()), default=0)
    longest_sensor_id = max(sensor_data, key=lambda x: (sensor_data[x][1] - sensor_data[x][0]) if (sensor_data[x][1] - sensor_data[x][0]) == longest_duration else -1)

    return f"{longest_sensor_id}:{longest_duration}"
