import java.util.HashSet;
import java.util.Random;

public class RandomSetGenerator {
    public static void randomSet(int min, int max, int n, HashSet<Integer> set) {
        if (n > (max - min + 1) || max < min) {
            return; // Unable to generate n distinct integers within the given range
        }

        Random random = new Random();
        set.clear(); // Clear the set before generating new random integers

        while (set.size() < n) {
            int randomNumber = random.nextInt((max - min) + 1) + min; // Generate a random number within the range [min, max]
            set.add(randomNumber); // Add the random number to the set (HashSet ensures distinct values)
        }
    }

    public static void main(String[] args) {
        HashSet<Integer> resultSet = new HashSet<>();
        randomSet(1, 10, 5, resultSet);
        System.out.println("Random Set: " + resultSet);
    }
}
