import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.atomic.AtomicInteger;

class BankAccount {
    private final int accountNumber;
    private double balance;

    public BankAccount(int accountNumber) {
        this.accountNumber = accountNumber;
        this.balance = 0;
    }

    public int getAccountNumber() {
        return accountNumber;
    }

    public double getBalance() {
        return balance;
    }

    public void deposit(double amount) {
        balance += amount;
    }

    public boolean withdraw(double amount) {
        if (balance >= amount) {
            balance -= amount;
            return true;
        }
        return false; // Insufficient balance
    }
}

public class BankingSystem {
    private final Map<Integer, BankAccount> accounts;
    private final AtomicInteger accountNumberGenerator;

    public BankingSystem() {
        this.accounts = new HashMap<>();
        this.accountNumberGenerator = new AtomicInteger(1);
    }

    public int createAccount() {
        int accountNumber = accountNumberGenerator.getAndIncrement();
        accounts.put(accountNumber, new BankAccount(accountNumber));
        return accountNumber;
    }

    public void deposit(int accountNumber, double amount) {
        BankAccount account = accounts.get(accountNumber);
        if (account != null) {
            account.deposit(amount);
        }
    }

    public boolean withdraw(int accountNumber, double amount) {
        BankAccount account = accounts.get(accountNumber);
        return account != null && account.withdraw(amount);
    }

    public boolean transfer(int fromAccountNumber, int toAccountNumber, double amount) {
        BankAccount fromAccount = accounts.get(fromAccountNumber);
        BankAccount toAccount = accounts.get(toAccountNumber);
        if (fromAccount != null && toAccount != null) {
            if (fromAccount.withdraw(amount)) {
                toAccount.deposit(amount);
                return true;
            }
        }
        return false; // Transfer failed
    }

    public double totalBalance() {
        return accounts.values().stream().mapToDouble(BankAccount::getBalance).sum();
    }
}
