public class Library {
    private Map<String, Book> books;

    public Library() {
        this.books = new HashMap<>();
    }

    public void addBook(String title, String author) {
        String key = title.toLowerCase();
        if (books.containsKey(key)) {
            // Book with the same title already exists, add with author's name
            key = title.toLowerCase() + "_" + author.toLowerCase();
        }
        books.put(key, new Book(title, author));
    }

    public void borrowBook(String title) {
        String key = title.toLowerCase();
        if (books.containsKey(key)) {
            Book book = books.get(key);
            if (book.isAvailable()) {
                book.setAvailable(false);
                System.out.println("Book '" + title + "' has been borrowed.");
            } else {
                System.out.println("Book '" + title + "' is already borrowed.");
            }
        } else {
            System.out.println("Book '" + title + "' does not exist in the library.");
        }
    }

    public void returnBook(String title) {
        String key = title.toLowerCase();
        if (books.containsKey(key)) {
            Book book = books.get(key);
            if (!book.isAvailable()) {
                book.setAvailable(true);
                System.out.println("Book '" + title + "' has been returned.");
            } else {
                System.out.println("Book '" + title + "' is already available.");
            }
        } else {
            System.out.println("Book '" + title + "' does not exist in the library.");
        }
    }

    public void displayAvailableBooks() {
        if (books.isEmpty()) {
            System.out.println("The library is empty.");
        } else {
            System.out.println("Available books in the library:");
            for (Book book : books.values()) {
                if (book.isAvailable()) {
                    System.out.println(book.getTitle() + " by " + book.getAuthor());
                }
            }
        }
    }

    public static void main(String[] args) {
        Library library = new Library();
        library.addBook("The Great Gatsby", "F. Scott Fitzgerald");
        library.addBook("To Kill a Mockingbird", "Harper Lee");
        library.addBook("The Great Gatsby", "Another Author");
        library.borrowBook("The Great Gatsby");
        library.borrowBook("To Kill a Mockingbird");
        library.borrowBook("Non-existent Book");
        library.returnBook("The Great Gatsby");
        library.returnBook("Non-existent Book");
        library.displayAvailableBooks();
    }
}
