import pickle
from scipy.sparse import csr_matrix
import os

class SparseMatrixIO:
    @staticmethod
    def save_matrix(matrix, filename):
        if os.path.exists(filename):
            overwrite = input(f"The file '{filename}' already exists. Do you want to overwrite it? (y/n): ")
            if overwrite.lower() != 'y':
                print("Matrix not saved. No changes made.")
                return

        try:
            with open(filename, 'wb') as file:
                pickle.dump(matrix, file)
            print(f"Matrix saved successfully to '{filename}'.")
        except Exception as e:
            print(f"Error occurred while saving the matrix to '{filename}': {e}")

    @staticmethod
    def load_matrix(filename):
        try:
            with open(filename, 'rb') as file:
                matrix = pickle.load(file)
                if not isinstance(matrix, csr_matrix):
                    raise TypeError("The loaded object is not a valid csr_matrix.")
                print(f"Matrix loaded successfully from '{filename}'.")
                return matrix
        except FileNotFoundError:
            print(f"Error: File '{filename}' not found.")
        except pickle.UnpicklingError:
            print(f"Error: Invalid pickled object in file '{filename}'.")
        except Exception as e:
            print(f"Error occurred while loading the matrix from '{filename}': {e}")
```

