from typing import List

def calculate_checksum(strings: List[str]) -> int:
    twos = 0
    threes = 0

    for string in strings:
        counts = {}
        for char in string:
            counts[char] = counts.get(char, 0) + 1

        if 2 in counts.values():
            twos += 1
        if 3 in counts.values():
            threes += 1

    checksum = twos * threes
    return checksum
