```java
import java.util.*;

public class ResourceProcessDefinitionUtils {
    public static Map<Integer, Set<Integer>> getResourceProcessDefinitionMap(List<Map<String, Object>> list) {
        Map<Integer, Set<Integer>> resourceProcessMap = new HashMap<>();

        for (Map<String, Object> map : list) {
            for (Map.Entry<String, Object> entry : map.entrySet()) {
                String processId = entry.getKey();
                List<Integer> resourceIds = (List<Integer>) entry.getValue();
                for (Integer resourceId : resourceIds) {
                    resourceProcessMap.computeIfAbsent(resourceId, k -> new HashSet<>()).add(Integer.parseInt(processId.replaceAll("[^0-9]", "")));
                }
            }
        }

        return resourceProcessMap;
    }
}
```

